$("#cartCheckoutForm").submit(function(){
    if($(".cart-item-required").length===0){
        event.preventDefault();
        alert('Please Add Some Items Before Checking Out :(')
    }
})
$(".add-to-cart").click(function (){
    $("#addToCart").click();
})
function galleryBtnAddToCart(){
    $(document).ready(function(){
        $('.galleryAddToCartBtn').click(function (){
            $('#addToCart').click();
        })
    })
}
function addToCartAjaxCallReady(successMsg){
    $(document).ready(function(){
        $("#addToCartForm, .addToCartForm").on("submit",function(e){
            e.preventDefault();
            var form = $(this);
            console.log(form)
            
            var fd = new FormData(form[0]);
            fd.append("submit", "addToCart");
            
            var addToCartButtons = $(".add-to-cart");
            addToCartButtons.attr("disabled", true);
            addToCartButtons.css({"opacity":"0.5", "pointer-events":"none"});
            
            $.ajax({
                contentType:false,
                processData:false,
                type:"POST",
                url:"/assets/tools/add-to-cart.php",
                data:fd,
                success:function(data){
                    var result = JSON.parse(data);
                    if(result.passcheck==1){
                        $("#cartBody").load(location.href+" #cartBody>*");
                        swal(successMsg,{
                            icon:"success",
                        }).then(function(){
                            /* function hideVariation Modal is in variation modal js */
                            hideVariationModal();
                            /*hide then open cart so the mask can stay */
                            openCartWidget();
                            addToCartButtons.attr("disabled", false);
                            addToCartButtons.css({"opacity":"1", "pointer-events":"initial"});
                        })
                        
                    }else{
                        swal("ERROR",{
                            icon:"warning",
                        }).then(function(){
                            addToCartButtons.attr("disabled", false);
                            addToCartButtons.css({"opacity":"1", "pointer-events":"initial"});
                        })
                    }
                },
                error: function(){
                    addToCartButtons.attr("disabled", false);
                    addToCartButtons.css({"opacity":"1", "pointer-events":"initial"});
                }
            })
            
        })
    })
    
}